var gulp=require("gulp");var jshint=require("gulp-jshint");var bootlint=require("gulp-bootlint");var uglify=require("gulp-uglify");var rename=require("gulp-rename");var bootlint=require("gulp-bootlint");var html5lint=require("gulp-html5-lint");var checkPages=require("check-pages");gulp.task("default",["js","html","bootstrap","links","minify"]);gulp.task("js",function(){return gulp.src("src/**/*.js").pipe(jshint()).pipe(jshint.reporter("default"))});gulp.task("html",function(){return gulp.src(["*.html","examples/*.html"]).pipe(html5lint())});gulp.task("bootstrap",function(){return gulp.src(["*.html","examples/**/*.html"]).pipe(bootlint())});gulp.task("links",function(b){var a={pageUrls:["index.html","examples/basic.html","examples/clear-formatting.html","examples/events.html","examples/form-post.html","examples/formatblock-example.html","examples/html-editor.html","examples/multiple-editors.html","examples/simple-toolbar.html"],checkLinks:true,summary:true};checkPages(console,a,b)});gulp.task("minify",function(){return gulp.src("src/*.js").pipe(uglify()).pipe(rename("bootstrap-wysiwyg.min.js")).pipe(gulp.dest("js"))});gulp.task("watch",function(){gulp.watch(["src/*.js","index.html","examples/*.html"],["js","html","bootstrap","links","minify"])});