describe("angular easypiechart directive",function(){var b,a,c;beforeEach(module("easypiechart"));beforeEach(inject(function(e,d){c=d;b=e}));it("should have percentage default value 0",function(d){c.percent=null;var e=angular.element('<div easypiechart percent="percent" options="options"></div>');b(e)(c);c.$digest();expect(e.isolateScope().percent).toBe(0)});it("inserts the element with a canvas element",function(){c.percent=-45;c.options={};var d=angular.element('<div easypiechart percent="percent" options="options"></div>');b(d)(c);c.$digest();expect(d.html()).toContain("canvas")});it("gets the options right",function(d){c.percent=0;c.options={animate:{duration:0,enabled:false},barColor:"#2C3E50",scaleColor:false,lineWidth:20,lineCap:"circle"};var e=angular.element('<div easypiechart percent="percent" options="options"></div>');b(e)(c);c.$digest();expect(e.isolateScope().options.animate.duration).toBe(0);expect(e.isolateScope().options.lineCap).toBe("circle")});it("has its own default options",function(d){c.percent=0;c.options={};var e=angular.element('<div easypiechart percent="percent" options="options"></div>');b(e)(c);c.$digest();expect(e.isolateScope().options.size).toBe(110);expect(e.isolateScope().options.animate.enabled).toBe(true)});it("takes size option the right way",function(){c.percent=0;c.options={size:200};var d=angular.element('<div easypiechart percent="percent" options="options"></div>');b(d)(c);c.$digest();expect(d.html()).toContain('height="200"');expect(d.html()).toContain('width="200"')})});