describe("Unit testing jQuery version of easy pie chart",function(){var b;var a=function(c,d){c=c||{};d=d||'<span class="chart"></span>';return function(){b=$(d);$("body").append(b);b.easyPieChart(c)}};describe("initialize plugin",function(){beforeEach(a());it("should insert a canvas element",function(){expect(b.html()).toContain("canvas")})});describe("takes size option and",function(){var c;beforeEach(a({size:200}));beforeEach(function(){c=b.find("canvas")});it("set correct width",function(){expect(c.width()).toBe(200)});it("set correct height",function(){expect(c.height()).toBe(200)})});describe("options should be overwritable by data attributes",function(){var c;beforeEach(a({size:200},'<span class="chart" data-size="400"></span>'));beforeEach(function(){c=b.find("canvas")});it("overwrite width",function(){expect(c.width()).toBe(400)});it("overwrite height",function(){expect(c.height()).toBe(400)})})});