JQVMap.prototype.deselect=function(c,b){c=c.toLowerCase();b=b||jQuery("#"+this.getCountryId(c))[0];if(this.isSelected(c)){this.selectedRegions.splice(this.selectIndex(c),1);jQuery(this.container).trigger("regionDeselect.jqvmap",[c]);b.currentFillColor=b.getOriginalFill();b.setFill(b.getOriginalFill())}else{for(var a in this.countries){this.selectedRegions.splice(this.selectedRegions.indexOf(a),1);this.countries[a].currentFillColor=this.color;this.countries[a].setFill(this.color)}}};