VectorCanvas.prototype.setSize=function(d,a){if(this.mode==="svg"){this.canvas.setAttribute("width",d);this.canvas.setAttribute("height",a)}else{this.canvas.style.width=d+"px";this.canvas.style.height=a+"px";this.canvas.coordsize=d+" "+a;this.canvas.coordorigin="0 0";if(this.rootGroup){var e=this.rootGroup.getElementsByTagName("shape");for(var c=0,b=e.length;c<b;c++){e[c].coordsize=d+" "+a;e[c].style.width=d+"px";e[c].style.height=a+"px"}this.rootGroup.coordsize=d+" "+a;this.rootGroup.style.width=d+"px";this.rootGroup.style.height=a+"px"}}this.width=d;this.height=a};