module("Data adapters - Maximum input length");var MaximumInputLength=require("select2/data/maximumInputLength");var $=require("jquery");var Options=require("select2/options");var Utils=require("select2/utils");function MaximumInputStub(){this.called=false}MaximumInputStub.prototype.query=function(a,b){this.called=true};var MaximumInputData=Utils.Decorate(MaximumInputStub,MaximumInputLength);test("0 never displays the notice",function(a){var b=new Options({maximumInputLength:0});var c=new MaximumInputData(null,b);c.trigger=function(){a.ok(false,"No events should be triggered")};c.query({term:""});a.ok(c.called);c=new MaximumInputData(null,b);c.query({term:"test"});a.ok(c.called)});test("< 0 never displays the notice",function(a){var b=new Options({maximumInputLength:-1});var c=new MaximumInputData(null,b);c.trigger=function(){a.ok(false,"No events should be triggered")};c.query({term:""});a.ok(c.called);c=new MaximumInputData(null,b);c.query({term:"test"});a.ok(c.called)});test("triggers when input is too long",function(a){var b=new Options({maximumInputLength:1});var c=new MaximumInputData(null,b);c.trigger=function(){a.ok(true,"The event should be triggered.")};c.query({term:"no"});a.ok(!c.called,"The adapter should not be called")});test("does not trigger when equal",function(a){var b=new Options({maximumInputLength:10});var c=new MaximumInputData(null,b);c.trigger=function(){a.ok(false,"The event should not be triggered.")};c.query({term:"1234567890"});a.ok(c.called)});test("does not trigger when less",function(a){var b=new Options({maximumInputLength:10});var c=new MaximumInputData(null,b);c.trigger=function(){a.ok(false,"The event should not be triggered.")};c.query({term:"123"});a.ok(c.called)});test("works with null term",function(a){var b=new Options({maximumInputLength:1});var c=new MaximumInputData(null,b);c.trigger=function(){a.ok(false,"The event should not be triggered")};c.query({});a.ok(c.called)});