module("Data adapters - Minimum input length");var MinimumInputLength=require("select2/data/minimumInputLength");var $=require("jquery");var Options=require("select2/options");var Utils=require("select2/utils");function StubData(){this.called=false}StubData.prototype.query=function(a,b){this.called=true};var MinimumData=Utils.Decorate(StubData,MinimumInputLength);test("0 never displays the notice",function(a){var b=new Options({minimumInputLength:0});var c=new MinimumData(null,b);c.trigger=function(){a.ok(false,"No events should be triggered")};c.query({term:""});a.ok(c.called);c=new MinimumData(null,b);c.query({term:"test"});a.ok(c.called)});test("< 0 never displays the notice",function(a){var b=new Options({minimumInputLength:-1});var c=new MinimumData(null,b);c.trigger=function(){a.ok(false,"No events should be triggered")};c.query({term:""});a.ok(c.called);c=new MinimumData(null,b);c.query({term:"test"});a.ok(c.called)});test("triggers when input is not long enough",function(a){var b=new Options({minimumInputLength:10});var c=new MinimumData(null,b);c.trigger=function(){a.ok(true,"The event should be triggered.")};c.query({term:"no"});a.ok(!c.called)});test("does not trigger when equal",function(a){var b=new Options({minimumInputLength:10});var c=new MinimumData(null,b);c.trigger=function(){a.ok(false,"The event should not be triggered.")};c.query({term:"1234567890"});a.ok(c.called)});test("does not trigger when greater",function(a){var b=new Options({minimumInputLength:10});var c=new MinimumData(null,b);c.trigger=function(){a.ok(false,"The event should not be triggered.")};c.query({term:"12345678901"});a.ok(c.called)});test("works with null term",function(a){var b=new Options({minimumInputLength:1});var c=new MinimumData(null,b);c.trigger=function(){a.ok(true,"The event should be triggered")};c.query({});a.ok(!c.called)});