module("Dropdown - dropdownCssClass compatibility");var $=require("jquery");var Utils=require("select2/utils");var Options=require("select2/options");var Dropdown=require("select2/dropdown");var DropdownCSS=Utils.Decorate(Dropdown,require("select2/compat/dropdownCss"));test("all classes will be copied if :all: is used",function(c){var b=$('<select class="test copy works"></select>');var d=new Options({dropdownCssClass:":all:"});var a=new DropdownCSS(b,d);var e=a.render();c.ok(e.hasClass("test"));c.ok(e.hasClass("copy"));c.ok(e.hasClass("works"));c.ok(!e.hasClass(":all:"))});test(":all: can be used with other classes",function(c){var b=$('<select class="test copy works"></select>');var d=new Options({dropdownCssClass:":all: other"});var a=new DropdownCSS(b,d);var e=a.render();c.ok(e.hasClass("test"));c.ok(e.hasClass("copy"));c.ok(e.hasClass("works"));c.ok(e.hasClass("other"));c.ok(!e.hasClass(":all:"))});test("classes can be passed in as a string",function(c){var b=$('<select class="test copy works"></select>');var d=new Options({dropdownCssClass:"other"});var a=new DropdownCSS(b,d);var e=a.render();c.ok(e.hasClass("other"))});test("a function can be used based on the element",function(c){var b=$('<select class="test"></select>');var d=new Options({dropdownCssClass:function(f){return"function"}});var a=new DropdownCSS(b,d);var e=a.render();c.ok(e.hasClass("function"));c.ok(!e.hasClass("test"))});test(":all: works around custom adapters",function(c){var b=$('<select class="test"></select>');var d=new Options({dropdownCssClass:":all: something",adaptDropdownCssClass:function(f){return f+"-modified"}});var a=new DropdownCSS(b,d);var e=a.render();c.ok(e.hasClass("something"));c.ok(e.hasClass("test"));c.ok(e.hasClass("test-modified"))});module("Dropdown - adaptDropdownCss compatibility");test("only return when adapted",function(c){var b=$('<select class="original"></select>');var d=new Options({adaptDropdownCssClass:function(f){return"modified"}});var a=new DropdownCSS(b,d);var e=a.render();c.ok(!e.hasClass("original"));c.ok(e.hasClass("modified"))});