module("Dropdown - Stoping event propagation");var Dropdown=require("select2/dropdown");var StopPropagation=require("select2/dropdown/stopPropagation");var $=require("jquery");var Options=require("select2/options");var Utils=require("select2/utils");var CustomDropdown=Utils.Decorate(Dropdown,StopPropagation);var options=new Options();test("click event does not propagate",function(b){b.expect(1);var d=$("#qunit-fixture .event-container");var a=new MockContainer();var e=new CustomDropdown($("#qunit-fixture select"),options);var c=e.render();e.bind(a,d);d.append(c);d.on("click",function(){b.ok(false,"The click event should have been stopped")});c.trigger("click");b.ok(true,"Something went wrong if this failed")});