module("select2(val)");test("multiple elements with arguments works",function(b){var e=require("jquery");require("jquery.select2");var d=e("<select><option>1</option><option>2</option></select>");var c=d.clone();var a=d.add(c);a.select2();a.select2("val","2");b.equal(d.val(),"2","The call should change the value on the first element");b.equal(c.val(),"2","The call should also change the value on the second element")});