module("select2(data)");var $=require("jquery");var Select2=require("select2/core");var Options=require("select2/options");test("single default selection returned",function(b){var e=$('<select><option>One</option><option>Two</option><option value="3" selected>Three</option></select>');var d=new Options({});var a=new Select2(e,d);var c=a.data();b.equal(c.length,1,"The one selected item should be returned");var f=c[0];b.equal(f.id,"3","The first option was correct");b.equal(f.text,"Three","The first option was correct")});test("multiple default selections returned",function(b){var f=$('<select multiple><option selected>One</option><option>Two</option><option value="3" selected>Three</option></select>');var e=new Options({});var a=new Select2(f,e);var c=a.data();b.equal(c.length,2,"The two selected items should be returned");var g=c[0];b.equal(g.id,"One","The first option was correct");var d=c[1];b.equal(d.id,"3","The option value should be pulled correctly")});module("select2(val)");test("single value matches jquery value",function(b){var d=$('<select><option>One</option><option>Two</option><option value="3" selected>Three</option></select>');var c=new Options({});var a=new Select2(d,c);var e=a.val();b.equal(e,"3","The value should match the option tag attribute");b.equal(e,d.val(),"The value should match the jquery value")});test("multiple value matches the jquery value",function(b){var d=$('<select multiple><option selected>One</option><option>Two</option><option value="3" selected>Three</option></select>');var c=new Options({});var a=new Select2(d,c);var e=a.val();b.equal(e.length,2,"Two options should be selected");b.deepEqual(e,["One","3"],"The values should match the option tag attribute");b.deepEqual(e,d.val(),"The values should match the jquery values")});