module("Defaults - Ajax");test("options are merged recursively with default options",function(a){var d=require("select2/defaults");var b=250;var c="http://www.test.com";var e;d.set("ajax--delay",b);e=d.apply({ajax:{url:c}});a.equal(e.ajax.delay,b,"Ajax default options are present on the merged options");a.equal(e.ajax.url,c,"Ajax provided options are present on the merged options");d.reset()});