module("Options - Attributes");var $=require("jquery");var Options=require("select2/options");test("no nesting",function(a){var c=$('<select data-test="test"></select>');var b=new Options({},c);a.equal(b.get("test"),"test")});test("with nesting",function(a){var c=$('<select data-first--second="test"></select>');if(c[0].dataset==null){a.ok(true,"We can not run this test with jQuery 1.x if dataset is not implemented");return}var b=new Options({},c);a.ok(!(b.get("first-Second")));a.equal(b.get("first").second,"test")});test("overrides initialized data",function(a){var c=$('<select data-override="yes" data-data="yes"></select>');var b=new Options({options:"yes",override:"no"},c);a.equal(b.get("options"),"yes");a.equal(b.get("override"),"yes");a.equal(b.get("data"),"yes")});