module("Selection containers - Placeholders");var Placeholder=require("select2/selection/placeholder");var SingleSelection=require("select2/selection/single");var $=require("jquery");var Options=require("select2/options");var Utils=require("select2/utils");var SinglePlaceholder=Utils.Decorate(SingleSelection,Placeholder);var placeholderOptions=new Options({placeholder:{id:"placeholder",text:"This is the placeholder"}});test("normalizing placeholder ignores objects",function(a){var c=new SinglePlaceholder($("#qunit-fixture .single"),placeholderOptions);var b={id:"test",text:"testing"};var d=c.normalizePlaceholder(b);a.equal(b,d)});test("normalizing placeholder gives object for string",function(a){var b=new SinglePlaceholder($("#qunit-fixture .single"),placeholderOptions);var c=b.normalizePlaceholder("placeholder");a.equal(c.id,"");a.equal(c.text,"placeholder")});test("text is shown for placeholder option on single",function(b){var c=new SinglePlaceholder($("#qunit-fixture .single"),placeholderOptions);var a=c.render();c.update([{id:"placeholder"}]);b.equal(a.text(),"This is the placeholder")});test("placeholder is shown when no options are selected",function(b){var c=new SinglePlaceholder($("#qunit-fixture .multiple"),placeholderOptions);var a=c.render();c.update([]);b.equal(a.text(),"This is the placeholder")});