module("Selection containers - Stoping event propagation");var SingleSelection=require("select2/selection/single");var StopPropagation=require("select2/selection/stopPropagation");var $=require("jquery");var Options=require("select2/options");var Utils=require("select2/utils");var CutomSelection=Utils.Decorate(SingleSelection,StopPropagation);var options=new Options();test("click event does not propagate",function(c){c.expect(1);var e=$("#qunit-fixture .event-container");var b=new MockContainer();var d=new CutomSelection($("#qunit-fixture select"),options);var a=d.render();d.bind(b,e);e.append(a);e.on("click",function(){c.ok(false,"The click event should have been stopped")});a.trigger("click");c.ok(true,"Something went wrong if this failed")});